import HomeView from '../views/HomeView.vue'
import 计数器 from '../components/父组件.vue'
import 区块链 from '../components/fathercomp.vue'
import 实验8_1 from "../实验8/TabFather.vue";
import 实验8_2 from "../实验8/FatherComp.vue";
import 实验12 from "../components/popup.vue";
import 实验12_2 from '../components/father.vue';
const routes = [
  {
    path: '/',
    redirect: '/HomeView',
    component:'HomeView'
  },
  {
    path: '/计数器+1',
    name: '计数器',
    component:计数器,
    meta:{
      title:'请输入标题'
    }
 },
  {
    path: '/区块链',
    name: '区块链',
    component: 区块链,
    meta:{
      title:'区块链技术的应用场景'
    }
  },
  {
    path: '/实验8-1',
    name: '实验8-1',
    component: 实验8_1,
    meta:{
      title:'实验8的作业'
    }
  },
  {
    path: '/实验8-2',
    name: '实验8-2',
    component: 实验8_2,
    meta:{
      title:'实验8的作业'
    }
  },
  {
    path: '/实验12',
    name: '实验12',
    component: 实验12,
    meta:{
      title:'实验12的作业'
    }
  },
]

const router = createRouter({
  history: createWebHistory(
    // process.env.BASE_URL
    ),
  routes
})

router.beforeEach((to,form,next)=>{
  document.title=to.meta.title//读取路由元信息，并修改网页标题
  next()//跳转到指定页面
})

export default router
